import Modal from "../UI/Modal";
import classes from "./Cart.module.css";
import { useContext, useState } from "react";
import CartContext from "../../store/CartContext";
import CartItem from "./CartItem/CartItem";
import Checkout from "./Checkout";

const Cart = (props) => {
  console.log(props);
  const cartCtx = useContext(CartContext);
  const [isCheckout, setIsCheckout] =  useState(false)
  const cartItemRemoveHandler = (id) => {
      cartCtx.removeItem(id)
  };
  const cartItemAddHandler = (item) => {
      cartCtx.addItem({...item, amount: 1})
  };
  const onSubmitHandler = () => {
    setIsCheckout(true)
  }
  const totalAmmount = `₹ ${cartCtx.totalAmount.toFixed(2)}`;
  const hasItems = cartCtx.items.length > 0;
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
        <CartItem
          key={item.id}
          name={item.name}
          amount={item.amount}
          price={item.price}
          onRemove={cartItemRemoveHandler.bind(null, item)}
          onAdd={cartItemAddHandler.bind(null, item)}
        />
      ))}
    </ul>
  );
const modalAction =  <div className={classes.actions}>
<button
  className={classes["button--alt"]}
  onClick={props.hideCartHandler}
>
  Close
</button>
{hasItems && <button className={classes.button} onClick = {onSubmitHandler}>Order</button>}
</div>
  return (
    <Modal onClick={props.hideCartHandler}>
      {cartItems}
      <div className={classes.total}>
        <span> Total Amount</span>
        <span> {totalAmmount}</span>
      </div>
      { isCheckout && <Checkout onCancel = {props.hideCartHandler}/>}
      { !isCheckout && modalAction}
    </Modal>
  );
};

export default Cart;
