import Modal from "../UI/Modal";
import classes from "./Cart.module.css";
import { useContext } from "react";
import CartContext from "../../store/CartContext";
import CartItem from "./CartItem/CartItem";

const Cart = (props) => {
    const cartCtx = useContext(CartContext)
    console.log(cartCtx);
    const totalAmmount = `₹ ${cartCtx.totalAmount.toFixed(2)}`;
    const hasItems = cartCtx.items.length > 0;
  const cartItems = (
    <ul className={classes["cart-items"]}>
      {cartCtx.items.map((item) => (
       <CartItem key = {item.id} name = {item.name} amount= {item.amount} price = {item.amount} />
      ))}
    </ul>
  );

  return (
    <Modal onClick = {props.hideCartHandler}>
      {cartItems}
      <div className={classes.total}>
        <span> Total Amount</span>
        <span> {totalAmmount}</span>
      </div>
      <div className={classes.actions}>
        <button className={classes["button--alt"]} onClick = {props.hideCartHandler}>Close</button>
        {hasItems && <button className={classes.button}>Order</button>}

      </div>
    </Modal>
  );
};

export default Cart;
